// Code in JavaScript - Targeting Builder - v11 PERFECT FIX
// ⚠️ CRITICAL: Fixes duplicate platform bug and invalid position combinations

return items.map((item) => {
  const original = item.json || {};

  const placementRaw = (original.placement || original.Placements || '').toString();
  const platformRaw = (original.platform || original.Platform || '').toString().toLowerCase();
  const optimizationGoal = (original.optimization_goal || original['Optimization Goal'] || '').toString().toUpperCase();
  const destinationType = (original.destination_type || original['Conversion Location'] || '').toString().toUpperCase();

  const choices = placementRaw.split(',').map(s => s.trim()).filter(Boolean);

  let publisher_platforms = new Set(); // Use Set to prevent duplicates
  let facebook_positions = new Set();
  let instagram_positions = new Set();
  let device_platforms = ['mobile'];
  let geo_countries = ['TR'];

  // Parse platform first
  if (platformRaw.includes('both') || (platformRaw.includes('facebook') && platformRaw.includes('instagram'))) {
    publisher_platforms.add('facebook');
    publisher_platforms.add('instagram');
    device_platforms = ['mobile', 'desktop'];
  } else if (platformRaw.includes('facebook')) {
    publisher_platforms.add('facebook');
    device_platforms = ['mobile', 'desktop'];
  } else if (platformRaw.includes('instagram')) {
    publisher_platforms.add('instagram');
    device_platforms = ['mobile']; // Instagram is mobile-only
  }

  // Parse placements
  for (const c of choices) {
    const cLower = c.toLowerCase();

    // Facebook placements
    if (cLower.includes('facebook')) {
      publisher_platforms.add('facebook');
      if (cLower.includes('feed')) facebook_positions.add('feed');
      if (cLower.includes('story') || cLower.includes('stories')) facebook_positions.add('story');
      if (cLower.includes('reels')) facebook_positions.add('facebook_reels');
      if (cLower.includes('marketplace')) facebook_positions.add('marketplace');
    }

    // Instagram placements
    if (cLower.includes('instagram')) {
      publisher_platforms.add('instagram');
      if (cLower.includes('feed') || cLower.includes('stream')) instagram_positions.add('feed');
      if (cLower.includes('story') || cLower.includes('stories')) instagram_positions.add('story');
      if (cLower.includes('reels')) instagram_positions.add('reels');
      if (cLower.includes('explore')) instagram_positions.add('explore');
    }
  }

  // ⚠️ CRITICAL: Destination type restrictions
  // PHONE_CALL (CALLS) - Only Facebook Feed allowed
  if (destinationType === 'PHONE_CALL' || destinationType === 'CALLS') {
    publisher_platforms = new Set(['facebook']);
    facebook_positions = new Set(['feed']);
    instagram_positions = new Set(); // Clear Instagram
    device_platforms = ['mobile', 'desktop'];
  }

  // MESSAGING_APPS (MESSAGING) - Only Facebook allowed, no Instagram
  if (destinationType === 'MESSAGING_APPS' || destinationType === 'MESSAGING') {
    publisher_platforms = new Set(['facebook']);
    facebook_positions = new Set(['feed']);
    instagram_positions = new Set(); // Clear Instagram - Messenger doesn't support Instagram positions
    device_platforms = ['mobile', 'desktop'];
  }

  // ⚠️ CRITICAL: REACH optimization has limited Instagram positions
  // Only 'feed' and 'stream' (which is feed) are valid for REACH
  if (optimizationGoal === 'REACH') {
    // For REACH, only feed is reliably supported on Instagram
    if (instagram_positions.size > 0) {
      instagram_positions = new Set(['feed']);
    }
  }

  // Convert Sets to Arrays
  publisher_platforms = [...publisher_platforms];
  facebook_positions = [...facebook_positions];
  instagram_positions = [...instagram_positions];

  // Defaults if empty
  if (publisher_platforms.length === 0) {
    publisher_platforms = ['facebook', 'instagram'];
  }
  if (facebook_positions.length === 0 && publisher_platforms.includes('facebook')) {
    facebook_positions = ['feed'];
  }
  if (instagram_positions.length === 0 && publisher_platforms.includes('instagram')) {
    instagram_positions = ['feed'];
  }

  // Age validation (13-65)
  let age_min = parseInt(original.age_min || original.Age_Min || 18, 10);
  let age_max = parseInt(original.age_max || original.Age_Max || 65, 10);
  age_min = Math.max(13, Math.min(65, age_min));
  age_max = Math.max(age_min, Math.min(65, age_max));

  const targeting = {
    age_min,
    age_max,
    geo_locations: { countries: geo_countries },
    publisher_platforms,
    device_platforms,
    targeting_automation: { advantage_audience: 0 }
  };

  // Only add positions if platform is included
  if (facebook_positions.length > 0 && publisher_platforms.includes('facebook')) {
    targeting.facebook_positions = facebook_positions;
  }
  if (instagram_positions.length > 0 && publisher_platforms.includes('instagram')) {
    targeting.instagram_positions = instagram_positions;
  }

  // Preserve ALL original fields and add targeting
  item.json = {
    ...original,
    targeting: targeting,
    _body: { targeting },
    __debug_targeting_v11: {
      original_platform: platformRaw,
      original_placements: placementRaw,
      destination_type: destinationType,
      optimization_goal: optimizationGoal,
      final_platforms: publisher_platforms,
      final_fb_positions: facebook_positions,
      final_ig_positions: instagram_positions
    }
  };

  return item;
});

/**
 * Build Final Ad Set Body (CBO) - v25 FINAL FIX
 * FIX: Ad Set Name ile Mapping eşleştirmesi
 */

const metaItems = items;
const sourceItems = $('If Budget Level (Camp Budget)').all();

let allMappingItems = [];
try {
    allMappingItems = $('Mapping').all() || [];
} catch (e) {
    console.log('Mapping node erişilemedi');
}

const returnItems = [];

function clean(v) {
  if (typeof v === 'string') {
    return v.replace(/\u00A0/g, ' ').replace(/\r?\n/g, ' ').trim().replace(/^="\s*|\s*"$/g, '');
  }
  return v == null ? '' : v;
}

function getVal(obj, keyStr) {
    if (!obj) return undefined;
    const keys = Object.keys(obj);
    const key = keys.find(k => k.toLowerCase() === keyStr.toLowerCase().trim());
    return key ? obj[key] : undefined;
}

function parseDate(dateInput) {
  if (!dateInput) return undefined;
  const str = String(dateInput).trim();
  if (/^\d{1,2}\.\d{1,2}\.\d{4}/.test(str)) {
      const parts = str.split('.');
      return new Date(parts[2], parts[1] - 1, parts[0]).toISOString();
  }
  if (str.includes('/') && !str.includes('.')) {
      const d = new Date(str);
      return !isNaN(d.getTime()) ? d.toISOString() : undefined;
  }
  const num = Number(str);
  if (!isNaN(num) && num > 20000) {
      const epoch = new Date(Date.UTC(1899, 11, 30));
      return new Date(epoch.getTime() + num * 86400000).toISOString();
  }
  if (str.includes('T') && str.includes('-')) return str;
  return undefined;
}

function parseMoney(val) {
  if (!val) return 0;
  let s = String(val).trim();
  if (s.includes('.') && s.includes(',')) {
      s = s.replace(/\./g, '').replace(/,/g, '.');
  } else if (s.includes(',')) {
      s = s.replace(/,/g, '.');
  }
  const n = parseFloat(s);
  return (isNaN(n) || n <= 0) ? 0 : Math.round(n * 100);
}

function mapStrategy(userStrategy) {
    const s = clean(userStrategy).toUpperCase();
    if (s.includes('BID') && s.includes('CAP')) return 'LOWEST_COST_WITH_BID_CAP';
    if (s.includes('COST') && s.includes('CAP')) return 'COST_CAP';
    if (s.includes('TARGET')) return 'TARGET_COST';
    return 'LOWEST_COST_WITHOUT_CAP';
}

function mapConversionToDestination(convLoc) {
  const mapping = {
    'WEBSITE': 'WEBSITE',
    'CALLS': 'PHONE_CALL',
    'MESSAGING': 'MESSAGING_APPS',
    'ON_AD': 'ON_AD',
    'APP': 'APP',
    'NONE': 'WEBSITE'
  };
  return mapping[clean(convLoc).toUpperCase()] || 'WEBSITE';
}

function validateMetaParams(dType, bEvent, oGoal) {
  if (dType === 'PHONE_CALL') {
    return { billing_event: 'IMPRESSIONS', optimization_goal: 'QUALITY_CALL', forceFB: true };
  }
  if (dType === 'MESSAGING_APPS') {
    const goal = (oGoal === 'LINK_CLICKS') ? 'CONVERSATIONS' : oGoal;
    return { billing_event: 'IMPRESSIONS', optimization_goal: goal, forceFB: true };
  }
  if (dType === 'ON_AD') {
    return {
      billing_event: 'IMPRESSIONS',
      optimization_goal: ['LEAD_GENERATION', 'REACH'].includes(oGoal) ? oGoal : 'LEAD_GENERATION',
      forceFB: false
    };
  }
  let finalBilling = bEvent;
  if (dType === 'WEBSITE') {
    if (['CONVERSIONS', 'VALUE', 'LANDING_PAGE_VIEWS'].includes(oGoal)) finalBilling = 'IMPRESSIONS';
    if (!['IMPRESSIONS', 'LINK_CLICKS'].includes(finalBilling)) finalBilling = 'IMPRESSIONS';
  }
  return { billing_event: finalBilling, optimization_goal: oGoal, forceFB: false };
}

function buildPromotedObject(destType, optGoal, pageId, pixelId) {
  if (destType === 'PHONE_CALL' && pageId) return { page_id: pageId };
  if (destType === 'ON_AD' && pageId) return { page_id: pageId };
  if (destType === 'WEBSITE' && pixelId) return { pixel_id: pixelId, custom_event_type: 'OTHER' };
  if (optGoal === 'LEAD_GENERATION' && pageId) return { page_id: pageId };
  return null;
}

// ============================================================
// 🔧 AD SET NAME İLE MAPPING'DEN DOĞRU SATIRI BUL
// ============================================================
function findMappingByAdSetName(adsetName, mappingItems) {
    if (!adsetName) return null;
    const targetName = clean(adsetName).toLowerCase().trim();

    for (let j = 0; j < mappingItems.length; j++) {
        const m = mappingItems[j];
        const name1 = clean(m.json.adset_name || '').toLowerCase().trim();
        const name2 = clean(m.json['Ad Set Name'] || '').toLowerCase().trim();

        if (name1 === targetName || name2 === targetName) {
            return m.json;
        }
    }
    return null;
}

// ============================================================
// MAIN LOOP
// ============================================================
for (let i = 0; i < metaItems.length; i++) {
    try {
        const metaJson = metaItems[i].json;
        const sheetJson = sourceItems[i] ? sourceItems[i].json : {};

        if (!sheetJson || Object.keys(sheetJson).length === 0) {
            continue;
        }

        const campaignId = clean(metaJson.campaign_id || metaJson.id || metaJson['Campaign ID']);
        const adSetName = clean(getVal(sheetJson, 'Ad Set Name') || getVal(sheetJson, 'adset_name') || '');
        const status = clean(getVal(sheetJson, 'Status (Ad Set)') || 'PAUSED').toUpperCase();

        // ⚠️ AD SET NAME İLE MAPPING'DEN DOĞRU SATIRI BUL
        const mappingJson = findMappingByAdSetName(adSetName, allMappingItems);

        // ⚠️ BID STRATEGY & BID AMOUNT - Önce Mapping'den al!
        let rawStrategy = null;
        let rawAmount = null;

        if (mappingJson) {
            rawStrategy = mappingJson.bid_strategy || mappingJson['Bid Strategy'];
            rawAmount = mappingJson.bid_amount || mappingJson['Bid Amount'];
        }

        // Fallback: sheetJson'dan al
        if (!rawStrategy) rawStrategy = getVal(sheetJson, 'Bid Strategy');
        if (!rawAmount) rawAmount = getVal(sheetJson, 'Bid Amount');

        let apiStrategy = mapStrategy(rawStrategy);
        let bidAmountKurus = parseMoney(rawAmount);

        const convLoc = clean(getVal(sheetJson, 'Conversion Location') || 'WEBSITE').toUpperCase();
        const rawBilling = clean(getVal(sheetJson, 'Billing Event') || 'IMPRESSIONS').toUpperCase();
        const rawGoal = clean(getVal(sheetJson, 'Optimization Goal') || 'LINK_CLICKS').toUpperCase();
        const platformStr = clean(getVal(sheetJson, 'Platform') || 'Both').toLowerCase();
        const placementStr = clean(getVal(sheetJson, 'Placements') || 'AUTO').toLowerCase();

        const ageMinRaw = getVal(sheetJson, 'Age_Min') || getVal(sheetJson, 'age_min');
        const ageMaxRaw = getVal(sheetJson, 'Age_Max') || getVal(sheetJson, 'age_max');
        const endDateRaw = getVal(sheetJson, 'End Date') || getVal(sheetJson, 'end_time');
        const startDateRaw = getVal(sheetJson, 'Start Date') || getVal(sheetJson, 'start_time');

        const pixelId = clean(getVal(sheetJson, 'Pixel_ID') || '').replace(/\D/g, '');
        const pageId = clean(getVal(sheetJson, 'FB Page_ID') || '').replace(/\D/g, '');
        const accountId = clean(getVal(sheetJson, 'Account ID') || getVal(sheetJson, 'account_id'));

        const destType = mapConversionToDestination(convLoc);
        const validated = validateMetaParams(destType, rawBilling, rawGoal);

        const ageMin = Math.max(13, Math.min(65, parseInt(ageMinRaw) || 18));
        const ageMax = Math.max(ageMin, Math.min(65, parseInt(ageMaxRaw) || 65));

        const targeting = {
            geo_locations: { countries: ['TR'] },
            age_min: ageMin,
            age_max: ageMax,
            publisher_platforms: [],
            facebook_positions: [],
            instagram_positions: [],
            device_platforms: ['mobile', 'desktop'],
            targeting_automation: { advantage_audience: 0 }
        };

        let useFB = validated.forceFB || platformStr.includes('facebook') || platformStr.includes('both');
        let useIG = !validated.forceFB && (platformStr.includes('instagram') || platformStr.includes('both'));
        if (!useFB && !useIG) useFB = true;

        if (useFB) targeting.publisher_platforms.push('facebook');
        if (useIG) targeting.publisher_platforms.push('instagram');

        if (placementStr !== 'auto' && placementStr !== '') {
            if (useFB) {
                if (placementStr.includes('feed')) targeting.facebook_positions.push('feed');
                if (placementStr.includes('story') || placementStr.includes('stories')) targeting.facebook_positions.push('story');
                if (placementStr.includes('reels')) targeting.facebook_positions.push('reels');
                if (targeting.facebook_positions.length === 0) targeting.facebook_positions.push('feed');
            }
            if (useIG) {
                if (placementStr.includes('feed')) targeting.instagram_positions.push('feed');
                if (placementStr.includes('story') || placementStr.includes('stories')) targeting.instagram_positions.push('story');
                if (placementStr.includes('reels')) targeting.instagram_positions.push('reels');
                if (targeting.instagram_positions.length === 0) targeting.instagram_positions.push('feed');
            }
        } else {
            delete targeting.publisher_platforms;
            delete targeting.facebook_positions;
            delete targeting.instagram_positions;
            delete targeting.device_platforms;
        }

        const promotedObject = buildPromotedObject(destType, validated.optimization_goal, pageId, pixelId);

        const finalBody = {
            name: adSetName,
            campaign_id: campaignId,
            status: ['ACTIVE', 'PAUSED'].includes(status) ? status : 'PAUSED',
            start_time: parseDate(startDateRaw),
            end_time: parseDate(endDateRaw),
            billing_event: validated.billing_event,
            optimization_goal: validated.optimization_goal,
            destination_type: destType,
            targeting: targeting,
            bid_strategy: apiStrategy
        };

        // ⚠️ BID AMOUNT EKLE (eğer varsa)
        if (bidAmountKurus > 0) {
            finalBody.bid_amount = bidAmountKurus;
        }

        if (promotedObject) finalBody.promoted_object = promotedObject;

        // Sanitizer: bid_amount yoksa stratejiyi düzelt
        if (!finalBody.hasOwnProperty('bid_amount')) {
            finalBody.bid_strategy = 'LOWEST_COST_WITHOUT_CAP';
        } else if (finalBody.bid_strategy === 'LOWEST_COST_WITHOUT_CAP') {
            delete finalBody.bid_amount;
        }

        Object.keys(finalBody).forEach(k => {
          if (finalBody[k] === '' || finalBody[k] == null) delete finalBody[k];
        });

        returnItems.push({
            json: {
                _finalBody: finalBody,
                campaign_id: campaignId,
                campaign_name: clean(getVal(sheetJson, 'Campaign Name')),
                adset_name: adSetName,
                account_id: accountId,
                original_row_no: sheetJson['Row No'],
                _debug: {
                    index: i,
                    matchedAdSetName: adSetName,
                    mappingFound: !!mappingJson,
                    rawBidStrategy: rawStrategy,
                    rawBidAmount: rawAmount,
                    parsedBidAmountKurus: bidAmountKurus,
                    finalBidStrategy: finalBody.bid_strategy,
                    hasBidAmount: finalBody.hasOwnProperty('bid_amount')
                }
            }
        });

    } catch (err) {
        returnItems.push({
            json: { error: true, message: err.message, index: i }
        });
    }
}

return returnItems;

/**
 * Build Final Ad Set Body1 (ABO) - v21 ULTIMATE FIX
 * Same as CBO but with daily_budget at adset level
 * FIXES: 
 * - instagram_positions 'feed' (not 'stream')
 * - PHONE_CALL/MESSAGING forces Facebook only
 * - billing_event/optimization_goal validation
 * - promoted_object for required destinations
 */

const clean = v => {
  if (typeof v === 'string') {
    return v.replace(/\u00A0/g, ' ').replace(/\r?\n/g, ' ').trim().replace(/^="\s*|\s*"$/g, '');
  }
  return v == null ? '' : v;
};

const looseGet = (obj, keys) => {
  if (!obj) return undefined;
  const norm = s => String(s ?? '').toLowerCase().replace(/\u00A0/g, ' ').replace(/[^a-z0-9]/g, '').trim();
  const look = Object.fromEntries(Object.keys(obj || {}).map(k => [norm(k), k]));
  for (const k of keys) {
    const hit = look[norm(k)];
    if (hit) return obj[hit];
  }
  return undefined;
};

function mapConversionToDestination(convLoc) {
  const c = clean(convLoc).toUpperCase();
  if (/APP/.test(c)) return 'APP';
  if (/(MESSAGE|WHATSAPP|MESSENGER|DM|MESSAGING)/.test(c)) return 'MESSAGING_APPS';
  if (/(ON_FACEBOOK|PROFILE|ON_AD)/.test(c)) return 'ON_AD';
  if (/(CALL|PHONE|CALLS)/.test(c)) return 'PHONE_CALL';
  return 'WEBSITE';
}

function validateAndFixApiParams(destination_type, billing_event, optimization_goal) {
  let fb = billing_event, fo = optimization_goal;
  let forceFB = false;

  switch (destination_type) {
    case 'PHONE_CALL': 
      fb = 'IMPRESSIONS'; 
      fo = 'QUALITY_CALL'; 
      forceFB = true;
      break;
    case 'MESSAGING_APPS': 
      fb = 'IMPRESSIONS'; 
      if (!['CONVERSATIONS','LEAD_GENERATION','IMPRESSIONS'].includes(fo)) fo = 'CONVERSATIONS';
      forceFB = true;
      break;
    case 'ON_AD': 
    case 'ON_FACEBOOK': 
      fb = 'IMPRESSIONS'; 
      if (!['LEAD_GENERATION','POST_ENGAGEMENT','IMPRESSIONS','REACH','VIDEO_VIEWS'].includes(fo)) fo = 'LEAD_GENERATION'; 
      break;
    default: 
      if (!['IMPRESSIONS','LINK_CLICKS'].includes(fb)) fb = 'IMPRESSIONS'; 
      if (!['LINK_CLICKS','LANDING_PAGE_VIEWS','OFFSITE_CONVERSIONS','VALUE','IMPRESSIONS','REACH','CONVERSIONS'].includes(fo)) fo = 'LINK_CLICKS';
  }
  return { billing_event: fb, optimization_goal: fo, forceFB };
}

function getPromotedObject(dt, og, pixelId, pageId) {
  if (dt === 'PHONE_CALL' && pageId) return { page_id: pageId };
  if (dt === 'ON_AD' && pageId) return { page_id: pageId };
  if (og === 'LEAD_GENERATION' && pageId) return { page_id: pageId };
  if (['OFFSITE_CONVERSIONS','VALUE','CONVERSIONS'].includes(og) && pixelId) return { pixel_id: pixelId, custom_event_type: 'OTHER' };
  if (dt === 'WEBSITE' && pixelId) return { pixel_id: pixelId, custom_event_type: 'OTHER' };
  return null;
}

// ========== MAIN LOGIC ==========
const cur = item.json || {};
const idx = $itemIndex;

let originalData = {};

try {
  const budgetItems = $('If Budget Level (Camp Budget)').all() || [];
  // ABO: FALSE branch (index 1)
  if (budgetItems[idx] && budgetItems[idx].json) {
    originalData = budgetItems[idx].json;
  }
} catch (e) {}

if (Object.keys(originalData).length === 0) {
  try {
    const codeItems = $('Code in JavaScript').all() || [];
    const aboItems = codeItems.filter(it => {
      const bt = (it.json.budget_type || '').toLowerCase();
      return bt.includes('ad set');
    });
    if (aboItems[idx] && aboItems[idx].json) {
      originalData = aboItems[idx].json;
    }
  } catch (e) {}
}

let ACCOUNT_ID = '';
try { ACCOUNT_ID = $('Mapping')?.item?.json?.account_id || ''; } catch(e){}
const account_id = ACCOUNT_ID || originalData.account_id || originalData['Account ID'] || '';

const getData = (keys) => looseGet(originalData, keys);

const campaign_id = cur.campaign_id || cur['Campaign ID'] || '';
const campaign_name = cur.campaign_name || cur['Campaign Name'] || originalData.campaign_name || '';
const adset_name = getData(['Ad Set Name', 'adset_name']) || '';
const status_adset = (getData(['status_adset', 'Status (Ad Set)', 'status']) || 'PAUSED').toUpperCase();

const billing_event_raw = (getData(['billing_event', 'Billing Event']) || 'IMPRESSIONS').toUpperCase();
const optimization_goal_raw = (getData(['optimization_goal', 'Optimization Goal']) || 'LINK_CLICKS').toUpperCase();
let bid_strategy = getData(['bid_strategy', 'Bid Strategy']) || 'LOWEST_COST_WITHOUT_CAP';

const bid_amount_raw = getData(['bid_amount', 'Bid Amount', 'Bid Cap']);
let bid_amount = null;
if (bid_amount_raw && !isNaN(Number(bid_amount_raw)) && Number(bid_amount_raw) > 0) {
  bid_amount = Math.round(Number(bid_amount_raw) * 100);
}

const BID_REQUIRED = ['LOWEST_COST_WITH_BID_CAP', 'COST_CAP', 'TARGET_COST'];
if (BID_REQUIRED.includes(bid_strategy) && (!bid_amount || bid_amount <= 0)) {
  bid_strategy = 'LOWEST_COST_WITHOUT_CAP';
}

// ABO: Daily Budget at adset level (TRY → kuruş)
const daily_budget_raw = getData(['daily_budget', 'Daily Budget', 'Ad Set Budget']);
let daily_budget = null;
if (daily_budget_raw && !isNaN(Number(daily_budget_raw))) {
  daily_budget = Math.round(Number(daily_budget_raw) * 100);
  if (daily_budget < 10000) daily_budget = 10000;
}

let start_time = originalData.start_time || getData(['start_time', 'Start Date']) || '';
let end_time = originalData.end_time || getData(['end_time', 'End Date']) || '';
if (!start_time) start_time = new Date().toISOString();
if (!end_time) end_time = new Date(new Date(start_time).getTime() + 30*24*60*60*1000).toISOString();

let age_min = Number(getData(['age_min', 'Age_Min']) || 18);
let age_max = Number(getData(['age_max', 'Age_Max']) || 65);
age_min = Math.max(13, Math.min(65, age_min));
age_max = Math.max(age_min, Math.min(65, age_max));

const convCell = getData(['conversion_location', 'Conversion Location', 'destination_type']) || 'WEBSITE';
const destination_type = mapConversionToDestination(convCell);

const validated = validateAndFixApiParams(destination_type, billing_event_raw, optimization_goal_raw);
const billing_event = validated.billing_event;
const optimization_goal = validated.optimization_goal;
const forceFB = validated.forceFB;

const pixel_id = String(getData(['Pixel_ID', 'Pixel ID']) ?? '').replace(/[^\d]/g, '') || undefined;
const page_id = String(getData(['FB Page_ID', 'page_id']) ?? '').replace(/[^\d]/g, '') || undefined;
const promoted_object = getPromotedObject(destination_type, optimization_goal, pixel_id, page_id);

// Targeting
const platformRaw = clean(getData(['platform', 'Platform']) || '').toLowerCase();
