function clean(v) {
  if (typeof v === 'string') {
    return v.replace(/\u00A0/g, ' ').replace(/\r?\n/g, ' ').trim()
      .replace(/^="\s*|\s*"$/g, '');
  }
  return v == null ? '' : v;
}

function normalizeUrl(u) {
  if (typeof u !== 'string') return '';
  let s = clean(u);
  if (!s) return '';
  if (/^tel:/i.test(s)) return '';
  if (/^www\./i.test(s)) s = 'https://' + s;
  if (!/^https?:\/\//i.test(s)) s = 'https://' + s;
  try { return new URL(s).toString(); } catch { return s; }
}

function normalizePhone(input) {
  if (typeof input !== 'string') return '';
  let p = clean(input);
  if (p.toLowerCase().startsWith('tel:')) {
    let n = p.slice(4).replace(/[^0-9+]/g, '').replace(/^\++/, '+');
    if (n.charAt(0) !== '+') n = '+' + n;
    return 'tel:' + n;
  }
  let n = p.replace(/[^0-9+]/g, '').replace(/^\++/, '+');
  if (n.charAt(0) !== '+') n = '+' + n;
  return 'tel:' + n;
}

function looseGet(obj, keys) {
  if (!obj) return undefined;
  function norm(s) {
    return String(s == null ? '' : s).toLowerCase()
      .replace(/\u00A0/g, ' ').replace(/[^a-z0-9]/g, '').trim();
  }
  const look = {};
  Object.keys(obj).forEach(k => { look[norm(k)] = k; });
  for (const cand of keys) {
    const hit = look[norm(cand)];
    if (hit) return obj[hit];
  }
  return undefined;
}

function appendUtm(base, params) {
  const entries = Object.entries(params).filter(([, v]) => v != null && String(v).trim() !== '');
  try {
    const url = new URL(base);
    for (const [k, v] of entries) url.searchParams.set(k, v);
    return url.toString();
  } catch {
    if (!entries.length) return base;
    const qs = entries.map(([k, v]) => `${encodeURIComponent(k)}=${encodeURIComponent(v)}`).join('&');
    return base + (base.includes('?') ? '&' : '?') + qs;
  }
}

