/**
 * Build CBO Campaign Update Body - v9 PERFECT FIX
 * 
 * Meta API EDITABLE fields for Campaign UPDATE:
 * - name
 * - status
 * - daily_budget (CBO only!)
 * - lifetime_budget
 */

function clean(v) {
  if (typeof v === 'string') {
    return v.replace(/\u00A0/g, ' ').replace(/\r?\n/g, ' ').trim()
      .replace(/^="\s*|\s*"$/g, '');
  }
  return v == null ? '' : v;
}

function normalizeStatus(s) {
  const v = clean(s).toUpperCase();
  return ['ACTIVE', 'PAUSED', 'DELETED'].includes(v) ? v : '';
}

function convertToKurus(tryAmount) {
  if (tryAmount === '' || tryAmount == null) return null;
  const num = parseFloat(tryAmount);
  if (isNaN(num) || num < 1) return null;
  const kurus = Math.round(num * 100);
  return kurus >= 10000 ? String(kurus) : '10000';
}

const row = item.json || {};

// Only process CBO rows
const budgetType = clean(row['Budget'] || row.budget_type || '').toLowerCase();
if (budgetType !== 'campaign budget') {
  item.json._skipCampaignUpdate = true;
  item.json._campaignUpdateBody = {};
  return item;
}

// Campaign ID (required)
const campaignId = clean(row['Campaign ID'] || row.campaign_id || '');
if (!campaignId) {
  item.json._skipCampaignUpdate = true;
  item.json._campaignUpdateBody = {};
  return item;
}

// Build update body - ONLY EDITABLE FIELDS
const updateBody = {};

// Name
const name = clean(row['Campaign Name'] || row.campaign_name || '');
if (name) updateBody.name = name;

// Status
const status = normalizeStatus(row['Status (Campaign)'] || row.status_campaign || '');
if (status) updateBody.status = status;

// ⚠️ CBO: Budget is at campaign level
const dailyBudget = convertToKurus(row['Ad Set Budget'] || row.daily_budget || '');
const lifetimeBudget = convertToKurus(row['Lifetime Budget'] || row.lifetime_budget || '');
if (dailyBudget) updateBody.daily_budget = dailyBudget;
if (lifetimeBudget) updateBody.lifetime_budget = lifetimeBudget;

item.json.campaign_id = campaignId;
item.json._campaignUpdateBody = updateBody;
item.json._skipCampaignUpdate = Object.keys(updateBody).length === 0;

return item;

/**
 * Build Final Ad Set UPDATE Body (CBO) - v9 PERFECT FIX
 *
 * Meta API EDITABLE fields for AdSet UPDATE:
 * - name
 * - status
 * - start_time
 * - end_time
 * 
 * ⚠️ CBO: NO budget fields (budget is at campaign level)
 * ⚠️ NON-EDITABLE: billing_event, optimization_goal, bid_strategy, targeting
 */

function clean(v) {
  if (typeof v === 'string') {
    return v.replace(/\u00A0/g, ' ').replace(/\r?\n/g, ' ').trim()
      .replace(/^="\s*|\s*"$/g, '');
  }
  return v == null ? '' : v;
}

function convertExcelDateToISO(excelDate) {
  if (!excelDate || excelDate === '') return '';
  if (typeof excelDate === 'string' && excelDate.includes('T')) return excelDate;
  const num = Number(excelDate);
  if (isNaN(num)) return '';
  const epochStart = Date.UTC(1899, 11, 30);
  const millisPerDay = 86400000;
  return new Date(epochStart + num * millisPerDay).toISOString();
}

function normalizeStatus(s) {
  const v = clean(s).toUpperCase();
  return ['ACTIVE', 'PAUSED', 'DELETED'].includes(v) ? v : '';
}

const row = item.json || {};

// Check budget type - only process CBO
const budgetType = clean(row['Budget'] || row.budget_type || '').toLowerCase();
if (budgetType !== 'campaign budget') {
  item.json._skipAdsetUpdate = true;
  item.json._adsetUpdateBody = {};
  return item;
}

// Ad Set ID (required)
const adsetId = clean(row['Ad Set ID'] || row.adset_id || '');
if (!adsetId) {
  item.json._skipAdsetUpdate = true;
  item.json._adsetUpdateBody = {};
  return item;
}

// Build update body - ONLY EDITABLE FIELDS
const updateBody = {};

// Name
const name = clean(row['Ad Set Name'] || row.adset_name || '');
if (name) updateBody.name = name;

// Status
const status = normalizeStatus(row['Status (Ad Set)'] || row.status_adset || '');
if (status) updateBody.status = status;

// Dates
const startTime = convertExcelDateToISO(row['Start Date'] || row.start_time || '');
const endTime = convertExcelDateToISO(row['End Date'] || row.end_time || '');
if (startTime) updateBody.start_time = startTime;
if (endTime) updateBody.end_time = endTime;

// ⚠️ CBO: NO daily_budget - budget is at campaign level

item.json._adsetId = adsetId;
item.json._adsetUpdateBody = updateBody;
item.json._skipAdsetUpdate = Object.keys(updateBody).length === 0;

return item;

// Build Ads UPDATE Bodies - v10 PERFECT FIX
// ⚠️ CRITICAL: Sets account_id for HTTP Request URLs

const cur = item.json || {};

// Get Mapping row
const mappingItems = $items('Mapping', 0) || [];
const row = (mappingItems[$itemIndex] || {}).json || {};
const sheetItems = $items('Meta Ads Automation', 0) || [];
const sheetRow = (sheetItems[$itemIndex] || {}).json || {};

function cleanCell(v) {
  if (typeof v !== 'string') return v == null ? '' : String(v);
  return v.replace(/\u00A0/g, ' ').replace(/\r?\n/g, ' ').trim()
    .replace(/^="\s*|\s*"$/g, '');
}

function pickFirst(...vals) {
  for (const v of vals) {
    if (v !== undefined && v !== null && String(v).trim() !== '') return String(v).trim();
  }
  return '';
}

function normalizeUrl(u) {
  if (typeof u !== 'string') return '';
  let s = cleanCell(u);
  if (!s) return '';
  if (/^tel:/i.test(s)) return '';
  if (/^www\./i.test(s)) s = 'https://' + s;
  if (!/^https?:\/\//i.test(s)) s = 'https://' + s;
  try { return new URL(s).toString(); } catch { return s; }
}

function normalizePhone(input) {
  if (typeof input !== 'string') return '';
  let p = cleanCell(input);
  if (p.toLowerCase().startsWith('tel:')) {
    let n = p.slice(4).replace(/[^0-9+]/g, '').replace(/^\++/, '+');
    if (!n.startsWith('+')) n = '+' + n;
    return 'tel:' + n;
  }
  let n = p.replace(/[^0-9+]/g, '').replace(/^\++/, '+');
  if (!n.startsWith('+')) n = '+' + n;
  return 'tel:' + n;
}

function appendUtm(base, params) {
  const entries = Object.entries(params).filter(([, v]) => v != null && String(v).trim() !== '');
  try {
    const url = new URL(base);
    for (const [k, v] of entries) url.searchParams.set(k, v);
    return url.toString();
  } catch {
    if (!entries.length) return base;
    const qs = entries.map(([k, v]) => `${encodeURIComponent(k)}=${encodeURIComponent(v)}`).join('&');
    return base + (base.includes('?') ? '&' : '?') + qs;
  }
}

// ⚠️ CRITICAL: Get account_id for HTTP Request URL
const account_id = cleanCell(pickFirst(
  cur.account_id, cur['Account ID'],
  row.account_id, row['Account ID'],
  sheetRow.account_id, sheetRow['Account ID']
));

const adId = cleanCell(pickFirst(cur.ad_id, cur['AD ID'], row['AD ID'], row.ad_id, sheetRow['AD ID']));
const adsetId = cleanCell(pickFirst(cur.adset_id, cur['Ad Set ID'], row['Ad Set ID'], row.adset_id, sheetRow['Ad Set ID']));

const adName = cleanCell(pickFirst(cur.ad_name, cur['Ad Name'], row['Ad Name'], row.ad_name, sheetRow['Ad Name']));
const statusAd = cleanCell(pickFirst(cur.status_ad, cur['Status (Ad)'], row['Status (Ad)'], row.status_ad, sheetRow['Status (Ad)'])).toUpperCase();

const pageId = cleanCell(pickFirst(cur.fb_page_id, cur['FB Page_ID'], row['FB Page_ID'], row.fb_page_id, sheetRow['FB Page_ID'])).replace(/[^\d]/g, '');
const igUserId = cleanCell(pickFirst(cur.ig_user_id, cur['IG User_ID'], row['IG User_ID'], row.instagram_user_id, sheetRow['IG User_ID'])).replace(/[^\d]/g, '');

const primaryText = cleanCell(pickFirst(cur.primary_text, cur['Primary Text'], row['Primary Text'], sheetRow['Primary Text']));
const headline = cleanCell(pickFirst(cur.headline, cur['Headline'], row['Headline'], sheetRow['Headline']));
const description = cleanCell(pickFirst(cur.description, cur['Description'], row['Description'], sheetRow['Description']));
const urlRaw = cleanCell(pickFirst(cur.url, cur['URL'], row['URL'], row.url, sheetRow['URL']));
const ctaType = cleanCell(pickFirst(cur.call_to_action, cur['Call To Action'], row['Call To Action'], sheetRow['Call To Action']) || 'LEARN_MORE').toUpperCase();
const callNumber = cleanCell(pickFirst(cur.call_number, cur['Call Number'], row['Call Number'], sheetRow['Call Number']));

// Build URL with UTM
const normalizedUrl = normalizeUrl(urlRaw);
const adsetName = cleanCell(pickFirst(cur.adset_name, cur['Ad Set Name'], row['Ad Set Name'], sheetRow['Ad Set Name']));
const platformRaw = cleanCell(pickFirst(cur.platform, cur['Platform'], row['Platform'], sheetRow['Platform']) || 'meta').toLowerCase();

const finalUrl = normalizedUrl ? appendUtm(normalizedUrl, {
  utm_source: 'meta',
  utm_medium: platformRaw.includes('instagram') && platformRaw.includes('facebook') ? 'both' :
              platformRaw.includes('instagram') ? 'instagram' : 'facebook',
  utm_campaign: 'ads',
  utm_content: adsetName || adName
}) : '';

// CTA
let callToAction;
if (ctaType === 'CALL_NOW' && callNumber) {
  callToAction = { type: 'CALL_NOW', value: { link: normalizePhone(callNumber) } };
} else {
