/**
 * Build Campaign Body (CBO) - v10 PERFECT FIX
 * Output: item.json._campaignBody
 * Also passes through account_id for URL
 */

function clean(v) {
  if (typeof v !== 'string') return v == null ? '' : String(v);
  return v.replace(/\u00A0/g, ' ').replace(/\r?\n/g, ' ').trim()
    .replace(/^="\s*/, '').replace(/"\s*$/, '').replace(/^=\s*/, '');
}

function mapObjective(raw) {
  const v = clean(raw).toUpperCase().replace(/\s+/g, '');

  if (v.startsWith('OUTCOME_')) {
    const valid = ['OUTCOME_AWARENESS', 'OUTCOME_ENGAGEMENT', 'OUTCOME_TRAFFIC',
      'OUTCOME_LEADS', 'OUTCOME_SALES', 'OUTCOME_APP_PROMOTION'];
    return valid.includes(v) ? v : 'OUTCOME_TRAFFIC';
  }

  const mapping = {
    'ENGAGEMENT': 'OUTCOME_ENGAGEMENT', 'MESSAGES': 'OUTCOME_ENGAGEMENT',
    'TRAFFIC': 'OUTCOME_TRAFFIC', 'LINK_CLICKS': 'OUTCOME_TRAFFIC',
    'LEADS': 'OUTCOME_LEADS', 'LEAD_GENERATION': 'OUTCOME_LEADS',
    'SALES': 'OUTCOME_SALES', 'CONVERSIONS': 'OUTCOME_SALES',
    'AWARENESS': 'OUTCOME_AWARENESS', 'BRAND_AWARENESS': 'OUTCOME_AWARENESS',
    'REACH': 'OUTCOME_AWARENESS', 'VIDEO_VIEWS': 'OUTCOME_AWARENESS',
    'APP_PROMOTION': 'OUTCOME_APP_PROMOTION', 'APP_INSTALLS': 'OUTCOME_APP_PROMOTION'
  };

  return mapping[v] || 'OUTCOME_TRAFFIC';
}

function normalizeStatus(s) {
  const v = clean(s).toUpperCase();
  return ['ACTIVE', 'PAUSED', 'DELETED'].includes(v) ? v : 'PAUSED';
}

function convertToKurus(tryAmount) {
  if (tryAmount === '' || tryAmount == null) return null;
  const num = parseFloat(tryAmount);
  if (isNaN(num) || num < 1) return null;
  const kurus = Math.round(num * 100);
  return kurus >= 10000 ? String(kurus) : '10000';
}

function parseSpecialCategories(raw) {
  const v = clean(raw).toUpperCase();
  if (!v || v === 'NONE' || v === '[]') return [];
  const valid = ['HOUSING', 'EMPLOYMENT', 'CREDIT', 'ISSUES_ELECTIONS_POLITICS'];
  return v.split(',').map(s => s.trim()).filter(c => valid.includes(c));
}

const row = item.json || {};

// ⚠️ CRITICAL: Pass through account_id for HTTP Request URL
const account_id = clean(row['Account ID'] || row.account_id || '');
item.json.account_id = account_id;

// Budget type check - only process CBO
const budgetType = clean(row['Budget'] || row.budget_type || '').toLowerCase();
if (budgetType !== 'campaign budget') {
  item.json._skipCampaign = true;
  return item;
}

// Build campaign body
const campaignName = clean(row['Campaign Name'] || row.campaign_name);
const objective = mapObjective(row['Objective'] || row.objective);
const status = normalizeStatus(row['Status (Campaign)'] || row.status_campaign || row.status);
const buyingType = clean(row['Buying Type'] || row.buying_type || 'AUCTION').toUpperCase();
const specialCategories = parseSpecialCategories(row['Special Ad Categories'] || row.special_ad_categories);

// CBO: Budget is at Campaign level
const dailyBudgetRaw = row['Ad Set Budget'] || row.daily_budget || '';
const lifetimeBudgetRaw = row['Lifetime Budget'] || row.lifetime_budget || '';

const campaignBody = {
  name: campaignName,
  objective: objective,
  status: status,
  buying_type: buyingType,
  special_ad_categories: specialCategories
};

// Add budget
const dailyBudgetKurus = convertToKurus(dailyBudgetRaw);
const lifetimeBudgetKurus = convertToKurus(lifetimeBudgetRaw);

if (dailyBudgetKurus) {
  campaignBody.daily_budget = dailyBudgetKurus;
} else if (lifetimeBudgetKurus) {
  campaignBody.lifetime_budget = lifetimeBudgetKurus;
}

// Bid strategy
const bidStrategy = clean(row['Bid Strategy'] || row.bid_strategy || '').toUpperCase();
if (bidStrategy && bidStrategy !== 'NONE') {
  campaignBody.bid_strategy = bidStrategy;
}

item.json._campaignBody = campaignBody;
item.json._skipCampaign = false;

return item;

/**
 * Build Campaign Body (ABO) - v10 PERFECT FIX
 * Output: item.json._campaignBody
 * ABO: NO budget at campaign level
 */

function clean(v) {
  if (typeof v !== 'string') return v == null ? '' : String(v);
  return v.replace(/\u00A0/g, ' ').replace(/\r?\n/g, ' ').trim()
    .replace(/^="\s*/, '').replace(/"\s*$/, '').replace(/^=\s*/, '');
}

function mapObjective(raw) {
  const v = clean(raw).toUpperCase().replace(/\s+/g, '');

  if (v.startsWith('OUTCOME_')) {
    const valid = ['OUTCOME_AWARENESS', 'OUTCOME_ENGAGEMENT', 'OUTCOME_TRAFFIC',
      'OUTCOME_LEADS', 'OUTCOME_SALES', 'OUTCOME_APP_PROMOTION'];
    return valid.includes(v) ? v : 'OUTCOME_TRAFFIC';
  }

  const mapping = {
    'ENGAGEMENT': 'OUTCOME_ENGAGEMENT', 'MESSAGES': 'OUTCOME_ENGAGEMENT',
    'TRAFFIC': 'OUTCOME_TRAFFIC', 'LINK_CLICKS': 'OUTCOME_TRAFFIC',
    'LEADS': 'OUTCOME_LEADS', 'LEAD_GENERATION': 'OUTCOME_LEADS',
    'SALES': 'OUTCOME_SALES', 'CONVERSIONS': 'OUTCOME_SALES',
    'AWARENESS': 'OUTCOME_AWARENESS', 'BRAND_AWARENESS': 'OUTCOME_AWARENESS',
    'REACH': 'OUTCOME_AWARENESS', 'VIDEO_VIEWS': 'OUTCOME_AWARENESS',
    'APP_PROMOTION': 'OUTCOME_APP_PROMOTION', 'APP_INSTALLS': 'OUTCOME_APP_PROMOTION'
  };

  return mapping[v] || 'OUTCOME_TRAFFIC';
}

function normalizeStatus(s) {
  const v = clean(s).toUpperCase();
  return ['ACTIVE', 'PAUSED', 'DELETED'].includes(v) ? v : 'PAUSED';
}

function parseSpecialCategories(raw) {
  const v = clean(raw).toUpperCase();
  if (!v || v === 'NONE' || v === '[]') return [];
  const valid = ['HOUSING', 'EMPLOYMENT', 'CREDIT', 'ISSUES_ELECTIONS_POLITICS'];
  return v.split(',').map(s => s.trim()).filter(c => valid.includes(c));
}

const row = item.json || {};

// ⚠️ CRITICAL: Pass through account_id for HTTP Request URL
const account_id = clean(row['Account ID'] || row.account_id || '');
item.json.account_id = account_id;

// Budget type check - only process ABO
const budgetType = clean(row['Budget'] || row.budget_type || '').toLowerCase();
if (budgetType !== 'ad set budget') {
  item.json._skipCampaign = true;
  return item;
}

// Build campaign body - NO BUDGET for ABO
const campaignName = clean(row['Campaign Name'] || row.campaign_name);
const objective = mapObjective(row['Objective'] || row.objective);
const status = normalizeStatus(row['Status (Campaign)'] || row.status_campaign || row.status);
const buyingType = clean(row['Buying Type'] || row.buying_type || 'AUCTION').toUpperCase();
const specialCategories = parseSpecialCategories(row['Special Ad Categories'] || row.special_ad_categories);

const campaignBody = {
  name: campaignName,
  objective: objective,
  status: status,
  buying_type: buyingType,
  special_ad_categories: specialCategories
};

item.json._campaignBody = campaignBody;
item.json._skipCampaign = false;

return item;

// ⚠️ Input Validation - v9 PERFECT FIX
// Enhanced validation with duplicate prevention and comprehensive checks

const staticData = ($workflow.getStaticData && $workflow.getStaticData('global')) || ($workflow.staticData ||= {});
staticData.errors = staticData.errors || [];
staticData.processedKeys = staticData.processedKeys || {};

function clean(v) {
  if (typeof v !== 'string') return v == null ? '' : String(v);
  return v.replace(/\u00A0/g, ' ').replace(/\r?\n/g, ' ').trim().replace(/^="\s*|\s*"$/g, '');
}

function validateRow(row, idx) {
  const errors = [];
  const warnings = [];

  // Required fields
  const accountId = clean(row['Account ID'] || row.account_id || '');
  const campaignName = clean(row['Campaign Name'] || row.campaign_name || '');
  const adsetName = clean(row['Ad Set Name'] || row.adset_name || '');
  const action = clean(row['Action'] || row.action || '').toLowerCase();

  if (!accountId) errors.push('Account ID is required');
  if (!campaignName && action.includes('create')) errors.push('Campaign Name is required for create');
  if (!adsetName && action.includes('create')) errors.push('Ad Set Name is required for create');

  // Duplicate check key
  const key = `${accountId}-${campaignName}-${adsetName}-${action}`;
  if (staticData.processedKeys[key]) {
    errors.push('Duplicate row detected - already processed');
  } else {
    staticData.processedKeys[key] = Date.now();
  }

  // Age validation
  const ageMin = parseInt(row['Age_Min'] || row.age_min || 18, 10);
  const ageMax = parseInt(row['Age_Max'] || row.age_max || 65, 10);
  if (ageMin < 13) warnings.push('Age_Min adjusted to minimum 13');
  if (ageMax > 65) warnings.push('Age_Max adjusted to maximum 65');
  if (ageMin > ageMax) errors.push('Age_Min cannot be greater than Age_Max');

  // Budget validation
  const budgetType = clean(row['Budget'] || row.budget_type || '').toLowerCase();
  const dailyBudget = parseFloat(row['Ad Set Budget'] || row.daily_budget || 0);
  if (budgetType === 'ad set budget' && dailyBudget < 100) {
    warnings.push('Daily budget adjusted to minimum 100 TRY');
  }

  // Conversion Location validation
  const convLoc = clean(row['Conversion Location'] || row.conversion_location || '').toUpperCase();
  const billingEvent = clean(row['Billing Event'] || row.billing_event || '');
  const optGoal = clean(row['Optimization Goal'] || row.optimization_goal || '');

  if (convLoc.includes('CALL') && billingEvent === 'LINK_CLICKS') {
    warnings.push('CALLS destination requires IMPRESSIONS billing - will be auto-corrected');
  }

  // Page ID validation for certain destinations
  const pageId = clean(row['FB Page_ID'] || row.fb_page_id || '');
  if ((convLoc.includes('CALL') || optGoal === 'LEAD_GENERATION') && !pageId) {
    errors.push('FB Page_ID is required for CALL/LEAD destinations');
  }

  return { 
    isValid: errors.length === 0, 
    errors, 
    warnings,
    key 
  };
}

// Filter items
const results = [];
for (let i = 0; i < items.length; i++) {
  const row = items[i].json;
  const validation = validateRow(row, i);

  if (validation.isValid) {
    items[i].json._validationPassed = true;
    items[i].json._warnings = validation.warnings;
    results.push(items[i]);
  } else {
    // Store error for reporting
    staticData.errors.push({
      row: i + 1,
      campaignName: row['Campaign Name'],
      errors: validation.errors
    });
  }
}

// Clean up old keys (older than 1 hour)
const oneHourAgo = Date.now() - 3600000;
Object.keys(staticData.processedKeys).forEach(k => {
  if (staticData.processedKeys[k] < oneHourAgo) {
    delete staticData.processedKeys[k];
  }
});

return results;
